import React,{useState} from 'react'
import { v4 } from 'uuid'
import { DragDropContext,Droppable,Draggable } from 'react-beautiful-dnd'
import InputBar from './InputBar'
import '../styles/MainScreen.css'


function MainScreen() {
    const [addTask,setAddTask] = useState('')
    const item = [
        {
            id:v4(),
            task:'Playing football with Putin ‚öΩ'
        },
        {
            id:v4(),
            task:'Makan ayam di MCD üçó'
        },
        {
            id:v4(),
            task:'Meeting dengan presiden Joko Widodo ü´±üèæ‚Äçü´≤üèΩ'
        }
    ]
    const [task,setTask] = useState(item)
    console.log(item)
  return (
    
    <div className='MainContainer'>
        <h1>Drag-n-Drop To-do List</h1>
        <InputBar addTask={addTask} setAddTask={setAddTask} item={item} setitem={setTask}/>
        <div>
            <DragDropContext onDragEnd={e=>console.log(e)}>
                <Droppable droppableId='task'>
                    {(provided)=>(
                        <div className='TaskBody' ref={provided.innerRef} {...provided.droppableProps}>
                            <ul>
                                {task.map(({id,task},idx)=>{
                                    return(
                                        <Draggable key={id} draggableId={id} index={idx}>
                                            {(provided)=>(
                                                <div ref={provided.innerRef} {...provided.dragHandleProps} {...provided.draggableProps}>
                                                    <li key={id} className="listContainer">
                                                        <p>{task}</p>
                                                    </li>
                                                </div>
                                            )}
                                        </Draggable>
                                    )
                                })}
                                {provided.placeholder}
                            </ul>
                        </div>
                    )}
                </Droppable>
            </DragDropContext>
        </div>
    </div>
  )
}

export default MainScreen
