import React from "react";
import "../styles/MainScreen.css";
import { GakGuna } from "../func/MainFunction";
import { v4 } from "uuid";

function InputBar({ addTask, setAddTask, item, setitem }) {
  return (
    <div className="AddSection">
      <input value={addTask} onChange={(e) => setAddTask(e.target.value)} />
      <button
        onClick={() => {
          const sourceColumn = item["Main-Todo"];
          const sourceitem = [...sourceColumn.items];
          console.log(sourceitem);
          console.log(sourceColumn);
          setitem({
            ...item,
            ["Main-Todo"]: {
              ...sourceColumn,
            },
          });
        }}
        className="AddButton"
      >
        Add Task
      </button>
    </div>
  );
}

export default InputBar;
