import React,{useState} from 'react'
import { v4 } from 'uuid'
import { DragDropContext,Droppable,Draggable } from 'react-beautiful-dnd'
import InputBar from './InputBar'
import { handleDragEnd } from '../func/MainFunction'
import '../styles/MainScreen.css'


function MainScreen() {
    const [addTask,setAddTask] = useState('')
    const [task,setTask] = useState([
        {
            id:v4(),
            task:'Main bola sama presiden Putin ⚽'
        },
        {
            id:v4(),
            task:'Makan ayam di MCD 🍗'
        },
        {
            id:v4(),
            task:'Meeting dengan presiden Joko Widodo 🫱🏾‍🫲🏽'
        }
    ])
    
    const [completed,setCompleted] = useState([
        {
            id:v4(),
            task:'Main basket sama Baim Wong 🏀'
        },
        {
            id:v4(),
            task:'Makan ikan di KFC 🐟'
        },
        {
            id:v4(),
            task:'Meeting dengan presiden Donal Trump 🫱🏾‍🫲🏽'
        }
    ])


    const handleOnDragnEnd = (res) =>{
        if(!res.destination) return
            console.log(res)
        const getDropableID = res.source.droppableId
        const getdestination = res.destination.droppableId
        console.log(getDropableID)
        console.log(getdestination)
        if(getDropableID === getdestination){
            const tempArray = Array.from()
        }
    }   

  return (
    
    <div className='MainContainer'>
        <h1>Drag-n-Drop To-do List</h1>
        <InputBar addTask={addTask} setAddTask={setAddTask} item={task} setitem={setTask}/>
        <div>
            <DragDropContext onDragEnd={handleOnDragnEnd}>
                <div className='section'>
                    <Droppable droppableId='task'>
                    {(provided)=>(
                        <div className='TaskBody' ref={provided.innerRef} {...provided.droppableProps}>
                            <h1>Current Task :</h1>
                            <ul>
                                {task.map(({id,task},idx)=>{
                                    return(
                                        <Draggable key={id} draggableId={id} index={idx}>
                                            {(provided)=>(
                                                <div ref={provided.innerRef} {...provided.dragHandleProps} {...provided.draggableProps}>
                                                    <li key={id} className="listContainer">
                                                        <p>{task}</p>
                                                    </li>
                                                </div>
                                            )}
                                        </Draggable>
                                    )
                                })}
                                {provided.placeholder}
                            </ul>
                        </div>
                    )}
                </Droppable>
                <Droppable droppableId='completed'>
                    {(provided)=>(
                        <div className='TaskBody' ref={provided.innerRef} {...provided.droppableProps}>
                            <h1>Completed Task :</h1>
                            <ul>
                                {completed.map(({id,task},idx)=>{
                                    return(
                                        <Draggable key={id} draggableId={id} index={idx}>
                                            {(provided)=>(
                                                <div ref={provided.innerRef} {...provided.dragHandleProps} {...provided.draggableProps}>
                                                    <li key={id} className="listContainer">
                                                        <p>{task}</p>
                                                    </li>
                                                </div>
                                            )}
                                        </Draggable>
                                    )
                                })}
                                {provided.placeholder}
                            </ul>
                        </div>
                    )}
                </Droppable>
                </div>
                
            </DragDropContext>
        </div>
    </div>
  )
}

export default MainScreen
