import React from 'react'
import '../styles/MainScreen.css'
import { addNewTask } from '../func/MainFunction'
import { v4 } from 'uuid'

function InputBar({addTask,setAddTask,item,setitem}) {
  return (
    <div className='AddSection'>
        <input value={addTask} onChange={(e)=>setAddTask(e.target.value)}/>
        <button 
        onClick={()=>{
          let TempData = {
            id:v4(),
            task:{addTask}
          }
            setitem(oldArray=>[{...oldArray,TempData}])
        }}
         className='AddButton'>Add Task</button>
    </div>
  )
}

export default InputBar
