import React from "react";
import "../styles/MainScreen.css";
import { GakGuna } from "../func/MainFunction";
import { v4 } from "uuid";
import { useState } from "react";

function InputBar({ addTask, setAddTask, item, setitem }) {
  const [openModal, setOpenModal] = useState(false);
  return (
    <div className="AddSection">
      <input value={addTask} onChange={(e) => setAddTask(e.target.value)} />
      <button
        onClick={() => {
          setitem((prev) => ({
            ...prev, // current kolom
            "Main-Todo": {
              ...prev["Main-Todo"], // current kolom["Main-Todo"]
              items: prev["Main-Todo"].items.concat({
                id: v4(),
                content: addTask,
              }), // new items
            },
          }));
        }}
        className="AddButton"
      >
        Add Task
      </button>
      <button className="AddButton">Add Column</button>
      {openModal && <Modal setModal={setOpenModal} />}
    </div>
  );
}

export default InputBar;
