import React, { useState } from "react";
import { v4 } from "uuid";
import { DragDropContext, Droppable, Draggable } from "react-beautiful-dnd";
import InputBar from "./InputBar";
import { handleDragEnd } from "../func/MainFunction";
import "../styles/MainScreen.css";

function MainScreen() {
  const [addTask, setAddTask] = useState("");

  const [kolom, setKolom] = useState({
    "Current Task": {
      name: "To-Do",
      items: [
        {
          id: v4(),
          content: "Main bola sama presiden Putin âš½",
        },
        {
          id: v4(),
          content: "Makan ayam di MCD ðŸ—",
        },
      ],
    },
  });

  const [item, setItem] = useState([
    {
      id: v4(),
      task: "Main bola sama presiden Putin âš½",
    },
    {
      id: v4(),
      task: "Makan ayam di MCD ðŸ—",
    },
    {
      id: v4(),
      task: "Meeting dengan presiden Joko Widodo ðŸ«±ðŸ¾â€ðŸ«²ðŸ½",
    },
  ]);

  const [complete, setCompleted] = useState([
    {
      id: v4(),
      task: "Main basket sama Baim Wong ðŸ€",
    },
    {
      id: v4(),
      task: "Makan ikan di KFC ðŸŸ",
    },
    {
      id: v4(),
      task: "Meeting dengan presiden Donal Trump ðŸ«±ðŸ¾â€ðŸ«²ðŸ½",
    },
  ]);

  const handleOnDragnEnd = (res) => {
    if (!res.destination) return;
    console.log(res);
    if (res.source.droppableId === "completed") {
      const tempArray = Array.from(complete);
      const [reorderedItem] = tempArray.splice(res.source.index, 1);
      if (res.destination.droppableId === "completed") {
        tempArray.splice(res.destination.index, 0, reorderedItem);
        setCompleted(tempArray);
      } else if (res.destination.droppableId === "task") {
        const taskArray = Array.from(item);
        taskArray.splice(res.destination.index, 0, reorderedItem);
        setItem(taskArray);
        setCompleted(tempArray);
      }
    } else if (res.source.droppableId === "task") {
      const tempArray = Array.from(item);
      const [reorderedItem] = tempArray.splice(res.source.index, 1);
      if (res.destination.droppableId === "task") {
        tempArray.splice(res.destination.index, 0, reorderedItem);
        setItem(tempArray);
      } else if (res.destination.droppableId === "completed") {
        const completedArray = Array.from(complete);
        completedArray.splice(res.destination.index, 0, reorderedItem);
        setCompleted(completedArray);
        setItem(tempArray);
      }
    }
  };

  return (
    <div className="MainContainer">
      <h1>Drag-n-Drop To-do List</h1>
      <InputBar
        addTask={addTask}
        setAddTask={setAddTask}
        item={item}
        setitem={setItem}
      />
      <div>
        <DragDropContext onDragEnd={handleOnDragnEnd}>
          <h1>Current Task :</h1>
          <div className="section">
            <Droppable droppableId="task">
              {(provided) => (
                <div
                  className="TaskBody"
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                >
                  <ul>
                    {item.map(({ id, task }, idx) => {
                      return (
                        <Draggable key={id} draggableId={id} index={idx}>
                          {(provided) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.dragHandleProps}
                              {...provided.draggableProps}
                            >
                              <li key={id} className="listContainer">
                                <p>{task}</p>
                              </li>
                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                    {provided.placeholder}
                  </ul>
                </div>
              )}
            </Droppable>
            <Droppable droppableId="completed">
              {(provided) => (
                <div
                  className="TaskBody"
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                >
                  <h1>Completed Task :</h1>
                  <ul>
                    {complete.map(({ id, task }, idx) => {
                      return (
                        <Draggable key={id} draggableId={id} index={idx}>
                          {(provided) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.dragHandleProps}
                              {...provided.draggableProps}
                            >
                              <li key={id} className="listContainer">
                                <p>{task}</p>
                              </li>
                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                    {provided.placeholder}
                  </ul>
                </div>
              )}
            </Droppable>
          </div>
        </DragDropContext>
      </div>
    </div>
  );
}

export default MainScreen;
